const initialState = {
    bedList : [],
    aminList: [],
    bookingCount : 0,
    bedprice: 0,
    aminprice: 0,
    totalprice: 0,
    temporary_value: 3,
}
export function addtoBed ( arr, obj ) {
    const index = arr.findIndex((e) => e.bed_id === obj.bed_id);

    if (index === -1) {
        arr.push(obj);
    } else {
        arr[index] = obj;
    }
      return arr
  }

export default function bedBookingReducer(state = initialState, action){
    switch(action.type){
        case 'ADD_BED': {
            // if(state.bedList.indexOf(action.payload) < -1)
             return {
                 ...state,
                 bedList: state.bedList.concat(addtoBed(state.bedList,action.payload)),
                // bedList: addtoBed(state.bedList,action.payload),
                 bedprice: state.bedprice + action.payload.price
             }}
        case 'ADD_AMIN' :
            return {
                ...state,
                aminprice: state.aminprice + action.payload.charge,
                aminList: state.aminList.concat(action.payload),
            }
        case 'BOOK_BED' :
            return {
                ...state,
                totalprice: state.bedprice + state.aminprice,
                bookingCount: state.bookingCount + 1,
            }
        case 'ADD_PRICE' :
            return {
                ...state,
                    totalprice: state.aminprice
            }
        default: return state     
    }
}
