import React, {Component} from 'react'
import Pgdetailslist from './Pgdetailslist'
import axios from 'axios'
import './menu.css';
import FilterList from '../filterList/filterList'
class PgDetails extends Component {
    constructor() {
         this.state = {
             database:[],
        }
    }
    componentDidMount() {
        axios.get(`http://localhost:8081/getpg?city=Pune&area=Hadapsar&start_date=2020-05-31&end_date=2020-06-29&no_of_guest=2`)
          .then(res => {
        //    console.log(res.data)
           this.state.database=res.data;
           this.setState({database:res.data})
            console.log(this.state.database)
          })
      }
    render() {
        return(
            <>
                   <div>
                       <FilterList />
                   </div>
                   <div>
                       <h2>dfd</h2>
                    {this.state.database.map((da,key)=>(
                        <Pgdetailslist data={this.state.database}></Pgdetailslist>
                        )
                        )}
                        </div>
            </>
        )
    }
}

export default PgDetails
