import React, { Component } from 'react'

export default class Roomdetaillist extends Component {
    koinahi = (event)=>{
            alert(event.target.value)
    }
    render() {
        return (
            <>
                {
                  Object.keys(this.props.data.rooms).map((data,key)=>(
                    <div className="dropdown">
                        <button className="dropbtn" key={key}>{this.props.data.rooms[data].id}</button>
                        <div className="dropdown-content">
                        {
                            Object.keys(this.props.data.rooms[data].bedList).map((d,key1)=>(
                                        <a href="#" key={key1} onClick={()=>this.props.addBed(this.props.data.rooms[data].bedList[d])}>{this.props.data.rooms[data].bedList[d].bed_id}</a>                          
                            ))
                        }
                        </div>
                   </div>))
                }
                <div className="amini-block">
                {
                    Object.keys(this.props.data.optionalAmenities).map((data,key)=>(
                        
                        <label className="amini-container">{this.props.data.optionalAmenities[data].name}
                        <input type="checkbox" value={this.props.data.optionalAmenities[data].charge} onClick={this.koinahi}/><span className="checkmark"></span>
                        </label>
                    ))
                }
                </div>
                
                {/* <div className="amini-block">
                    <label className="amini-container">News Paper
                        <input type="checkbox" />
                            <span className="checkmark"></span>
                    </label>
                 </div> */}
            </>
        )
    }
}
