import React, { Component } from 'react'

export default class Roomdetaillist extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             isCheck: false,
        }
    }
    isChecked = (event) =>{
            if(this.isCheck !=true){
                this.setState({
                    isCheck: true
                })
            } 
            else{
                this.setState({
                    isCheck: false
                })
            }
            
    }
    render() {
        return (
            <>
                {
                  Object.keys(this.props.data.rooms).map((data,key)=>(
                    <div className="dropdown">
                        <button className="dropbtn" key={key}>{this.props.data.rooms[data].id}</button>
                        <div className="dropdown-content">
                        {
                            Object.keys(this.props.data.rooms[data].bedList).map((d,key1)=>(
                                        <a href="#" key={key1} onClick={()=>this.props.addBed(this.props.data.rooms[data].bedList[d],this.props.data.rooms[data].rent_per_bed)}>{this.props.data.rooms[data].bedList[d].bed_id}</a>                          
                            ))
                        }
                        </div>
                   </div>))
                }
                <div className="amini-block">
                {
                    Object.keys(this.props.data.optionalAmenities).map((data,key)=>(
                        
                        <label className="amini-container">{this.props.data.optionalAmenities[data].name}
                        <input type="checkbox" onClick={()=>this.props.addAmin(this.props.data.optionalAmenities[data].charge)} onChange={this.isChecked} value={this.state.isCheck}/><span className="checkmark"></span>
                        </label>
                    ))
                }
                </div>
            </>
        )
    }
}
