import React, { Component } from 'react'
import './Home.css'
import Header from '../header/header'
import {NavLink} from 'react-router-dom'
import Citynames from '../data/city.json'

// import React from 'react'

export default class Homepage extends Component {
  constructor(props) {
    super(props)
  
    state = {
       cityname:'',
       cityarea:'',
       startdate:'',
       enddate:'',
       noofguest:0
    }
  }
  citynameHandler=(event)=>{
    this.setState({
      cityname:event.target.value.cityname,
      cityarea:event.target.value.cityarea,
      startdate:event.target.value.startdate,
      enddate:event.target.value.enddate,
      noofguest:event.target.value.noofguest
    })
  }
  cityareaHandler=(e)=>{
      this.setState({
        cityarea:e.target.value
      })
  }
  startdateHandler=(e)=>{
    this.setState({
      startdate:e.target.value
    })
  }
  enddateHandler=(e)=>{
    this.setState({
      enddate:e.target.value
    })
  }
  noofguestHandler=(e)=>{
    this.setState({
      noofguest:e.target.value
    })
  }
  clickHandler = () =>{
    alert(`${this.state.cityname} ${this.cityarea} ${this.state.startdate} ${this.state.enddate} ${this.state.noofguest}`)
  }
  
  render() {
    return (
      <>
        <div class="header-banner">
  <div class="container-width">
    <div class="clearfix">
    </div>
    <div class="lead-title">A Complete Solution
    </div>
    <form class="form">
      <label class="label">City</label>


      <select name="City" required class="select" value={cityname}><option value="">- Select option -</option>
            {
              Citynames.city.map((city,id)=>
            (<option key={id} value={city}>{city}</option>))
            }
      </select>
      <div class="form-group">
        <label class="label" >Area</label>
        <input type="text" value={cityarea} required type="text" class="input"/>
      </div>
      <div class="form-group">
        <label class="label">Start Date</label>
        <input type="date" value={startdate} required type="date" class="input"/>
      </div>
      <div class="form-group">
        <label class="label">End Date</label>
        <input type="date" value={enddate} placeholder="estimated" required type="date" class="input"/>
      </div>
      <div class="form-group">
        <label class="label">No. of Guest</label>
        <input type="number" value={noofguest} placeholder="Number Of Guest" required type="number" class="input"/>
      </div>
      <div class="form-group">
      </div>
      <div id="Go" title="Submit" class="form-group">
        <button type="submit" class="button">Go</button>
      </div>
    </form>
  </div>
        </div>
         {/* <div class="header-banner"> 
          <div class="conatainer-width">
            <div class="form">
            <label class="label">
              <NavLink to='/loginStudent'></NavLink>Click to Proceed
            </label>
            
            <NavLink to='/loginStudent'>
            <button class="button">Student
            </button></NavLink>
            <NavLink to='/loginadmin'>
            <button class="button">Manager/Admin</button>
            </NavLink>
          </div>
          </div> */}
        {/* </div> */}
      </>
    )
  }
}
