import React, {Component} from 'react'
import Pgdetailslist from './Pgdetailslist'
import axios from 'axios'
import './menu.css';
import FilterList from '../filterList/filterList'
// import {connect} from 'react-redux'
class PgDetails extends Component {
    constructor() {
        super()
         this.state = {
             data: [],
             name:''
        }
    }
    componentDidMount() {
        axios.get(`http://localhost:8081/getpg?city=Pune&area=Hadapsar&start_date=2020-05-31&end_date=2020-06-29&no_of_guest=2`)
          .then(res => {
        //    console.log(res.data)
           this.state.data=res.data;
           this.state.name=res.data[0].name
            console.log(this.state.name)
          })
      }
    render() {
        return(
            <>
                   <div>
                       <FilterList />
                       <Pgdetailslist></Pgdetailslist>
                   </div>
                   <div>
                    {this.state.data.map((key,da)=>(
                        <h2>{da.name}</h2>
                        )
                        )}
                        </div>
            </>
        )
    }
}

export default PgDetails
