const initialState = {
    bedList : [],
    aminList: [],
    bookingCount : 0,
    bedprice: 0,
    aminprice: 0,
    totalprice: 0,
    temporary_value: 30,
}

export default function bedBookingReducer(state = initialState, action){
    switch(action.type){
        case 'ADD_BED': 
             return {
                 ...state,
                 bookingCount: state.bookingCount + 1,
                 bedList: state.bedList.concat(action.payload.bed),
                 bedprice: state.bedprice + action.payload.price
             }
        case 'ADD_AMIN' :
            return {
                ...state,
                aminprice: state.aminprice + action.payload,
                aminList: state.aminList.concat(action.payload),
            }
        case 'BOOK_BED' :
            return {
                ...state,
                temporary_value: state.temporary_value
            }
        case 'ADD_PRICE' :
            return {
                ...state,
                    totalprice: state.aminprice
            }
        default: return state     
    }
}
