import React, {Component} from 'react'
import ReactDom from 'react-dom'
import './Home.css'
import Header from '../header/header'
import {NavLink} from 'react-router-dom'
import Citynames from '../data/city.json'


export default class Homepage extends Component {
  constructor(props) {
    super(props)
  
    this.state = {
       cityname:'varanasi',
       cityarea:'babatpur',
       startdate:'2020-06-03',
       enddate:'2020-03-03',
       noofguest:90,
    }
  }
  citynameHandler=(event)=>{
    this.setState({
      cityname:event.target.value
    })
  }
  cityareaHandler=(e)=>{
      this.setState({
        cityarea:e.target.value
      })
  }
  startdateHandler=(e)=>{
    this.setState({
      startdate:e.target.value
    })
  }
  enddateHandler=(e)=>{
    this.setState({
      enddate:e.target.value
    })
  }
  noofguestHandler=(e)=>{
    this.setState({
      noofguest:e.target.value
    })
  }
  clickHandler = () =>{
    alert(`${this.state.cityname} ${this.cityarea} ${this.state.startdate} ${this.state.enddate} ${this.state.noofguest}`)
  }
  
  render() {
    return (
      <>
        <div className="header-banner">
  <div className="container-width">
    <div className="clearfix">
    </div>
    <div className="lead-title">A Complete Solution
    </div>
    <form className="form">
      <label className="label">City</label>


      <select name="City" required className="select" value={this.cityname}><option value="">- Select option -</option>
            {
              Citynames.city.map((city,id)=>
            (<option key={id} value={city}>{city}</option>))
            }
      </select>
      <div classNameName="form-group">
        <label classNameName="label" >Area</label>
        <input type="text" value={this.cityarea} required type="text" className="input"/>
      </div>
      <div className="form-group">
        <label className="label">Start Date</label>
        <input type="date" value={this.startdate} required type="date" className="input"/>
      </div>
      <div className="form-group">
        <label className="label">End Date</label>
        <input type="date" value={this.enddate} placeholder="estimated" required type="date" className="input"/>
      </div>
      <div className="form-group">
        <label className="label">No. of Guest</label>
        <input type="number" value={this.noofguest} placeholder="Number Of Guest" required type="number" className="input"/>
      </div>
      <div className="form-group">
      </div>
      <div id="Go" title="Submit" className="form-group">
        <button type="submit" className="button" onClick={this.clickHandler}>Go</button>
      </div>
    </form>
  </div>
        </div>
         {/* <div className="header-banner"> 
          <div className="conatainer-width">
            <div className="form">
            <label className="label">
              <NavLink to='/loginStudent'></NavLink>Click to Proceed
            </label>
            
            <NavLink to='/loginStudent'>
            <button className="button">Student
            </button></NavLink>
            <NavLink to='/loginadmin'>
            <button className="button">Manager/Admin</button>
            </NavLink>
          </div>
          </div> */}
        {/* </div> */}
      </>
    )
  }
}
