const initialState = {
    bedList : [],
    bookingCount : 0,
}

export default function bedBookingReducer(state = initialState, action){
    switch(action.type){
        case 'ADD_BED': 
             return {
                 ...state,
                 bookingCount: state.bookingCount + 1,
                 bedList: state.bedList.concat(action.payload)
             }
        case 'REMOVE_BED' :
            return {
                ...state,
                bookingCount: state.bookingCount - 1,
                //reducer to remove bed
            }
        default: return state     
    }
}
