const initialState = {
    bedList : [],
    aminList: [],
    bookingCount : 0,
    bedprice: 0,
    aminprice: 0,
    totalprice: 0,
    bedCount: 0,
    temporary_value: 3,
    
}

export default function bedBookingReducer(state = initialState, action){
    switch(action.type){
        case 'ADD_BED': {
                    const {id} = action.payload.bed_id
                    const { bedList, bedprice,bookingCount } = state
                    let bedtoAdd = bedList[id]
                    let count
                    if(!bedtoAdd){
                        bedtoAdd = bedList.find(obj =>obj.id === id)
                            count = 1
                    }
                    else{
                        count = bedtoAdd.count + 1
                    }
                    const bid = bedtoAdd.id
                    const newCart = {
                        ...state.bedList, [bid]: {...bedtoAdd, count}
                    }
                    const newCount = bookingCount + 1
             return {
                 ...state,
                 bedList: newCart,
                 bookingCount: newCount,
                 bedprice: state.bedprice + action.payload.price
             }}
        case 'ADD_AMIN' :
            return {
                ...state,
                aminprice: state.aminprice + action.payload.charge,
                aminList: state.aminList.concat(action.payload),
            }
        case 'BOOK_BED' :
            return {
                ...state,
                totalprice: state.bedprice + state.aminprice,
                bookingCount: state.bookingCount + 1,
            }
        case 'ADD_PRICE' :
            return {
                ...state,
                    totalprice: state.aminprice
            }
        default: return state     
    }
}
